<?phpglobal $wpdb;$table5 = RCL_PREF."wallet_history";if($wpdb->get_var("show tables like '". $table5 . "'") != $table5) {        $wpdb->query("CREATE TABLE IF NOT EXISTS `". $table5 . "` (       ID bigint (20) NOT NULL AUTO_INCREMENT,       user_id INT(20) NOT NULL,       count_pay VARCHAR( 20 ) NOT NULL,       user_balance VARCHAR( 20 ) NOT NULL,       comment_pay TEXT NOT NULL,       time_pay DATETIME NOT NULL,       type_pay INT(20) NOT NULL,       UNIQUE KEY id (id)     ) DEFAULT CHARSET=utf8;");}$table5 = RCL_PREF."wallet_request";if($wpdb->get_var("show tables like '". $table5 . "'") != $table5) {	   $wpdb->query("CREATE TABLE IF NOT EXISTS `". $table5 . "` (	  ID bigint (20) NOT NULL AUTO_INCREMENT,	  user_rq INT(20) NOT NULL,	  count_rq VARCHAR(20) NOT NULL,	  output_rq VARCHAR(20) NOT NULL,	  comment_rq VARCHAR(100) NOT NULL,	  time_rq DATETIME NOT NULL,	  status_rq INT(20) NOT NULL,	  UNIQUE KEY id (id)	) DEFAULT CHARSET=utf8;");}